    $(document).ready(function () {

        $('#CountryPhonePrefixList').hide();
        setCountryPhonePrefix();

        $('#Country').change(function () {
            setCountryPhonePrefix();
        });

        function setCountryPhonePrefix() {
            var selectedCountryCode = $('#Country option:selected').val();
            var phonePrefix = $("#CountryPhonePrefixList option:contains('" + selectedCountryCode + "')").val();
            $('input#PhoneNumberCountryPrefix').val(phonePrefix);
            $('input#CountryCode').val('+' + phonePrefix);
        }
    });
</script>
