<script type="text/javascript">
    $(document).ready(function () {

        $('#SecureAcceptanceCountryPhonePrefixList').hide();
        fnSetSecureAcceptanceCountryPhonePrefix();

        $('#SecureAcceptanceCountry').change(function () {
            fnSetSecureAcceptanceCountryPhonePrefix();
        });
        

        $('#SecureAcceptanceCountryRegionUS').change(function () {
            var selectedSecureAcceptanceCountryCode = $('#SecureAcceptanceCountry option:selected').val();

            if (selectedSecureAcceptanceCountryCode == 'US') {
                var secureAcceptanceCountryRegionUS = $('#SecureAcceptanceCountryRegionUS option:selected').val();
                $('input#SecureAcceptanceState').val(secureAcceptanceCountryRegionUS);
            }
            else {
                $('input#SecureAcceptanceState').val('');
            }
        });
        

        $('#SecureAcceptanceCountryRegionCA').change(function () {
            var selectedSecureAcceptanceCountryCode = $('#SecureAcceptanceCountry option:selected').val();

            if (selectedSecureAcceptanceCountryCode == 'CA') {
                var secureAcceptanceCountryRegionCA = $('#SecureAcceptanceCountryRegionCA option:selected').val();
                $('input#SecureAcceptanceState').val(secureAcceptanceCountryRegionCA);
            }
            else {
                $('input#SecureAcceptanceState').val('');
            }
        });
        
 function fnSetSecureAcceptanceCountryPhonePrefix() {

            var selectedCountryCode = $('#SecureAcceptanceCountry option:selected').val();
            var secureAcceptancePhonePrefix = $("#SecureAcceptanceCountryPhonePrefixList option:contains('" + selectedCountryCode + "')").val();

            $('input#SecureAcceptancePhoneNumberCountryPrefix').val(secureAcceptancePhonePrefix);
            $('input#SecureAcceptanceCountryCode').val('+' + secureAcceptancePhonePrefix);

            var listShown = false;
            

            if (selectedCountryCode == 'US') {
                listShown = true;
                $('#SecureAcceptanceCountryRegionUS').show();
            }
            else {
                $('#SecureAcceptanceCountryRegionUS').hide();
            }
            

            if (selectedCountryCode == 'CA') {
                listShown = true;
                $('#SecureAcceptanceCountryRegionCA').show();
            }
            else {
                $('#SecureAcceptanceCountryRegionCA').hide();
            }
            

            if (!listShown) {
                

                if ($('input#SecureAcceptanceState').val() == $('#SecureAcceptanceCountryRegionUS option:selected').val()) {
                    $('input#SecureAcceptanceState').val('');
                }
                

                if ($('input#SecureAcceptanceState').val() == $('#SecureAcceptanceCountryRegionCA option:selected').val()) {
                    $('input#SecureAcceptanceState').val('');
                }
                

                $('#SecureAcceptanceState').show();
            }
            else { $('#SecureAcceptanceState').hide();
            }
        }
    });
</script>
